mapper.select:
		select is_private, password
      from `groups`
      where id = #{groupId}

select * from `groups`


		(group_name like concat('%', #{search}, '%') or content like concat('%', #{search}, '%'))



			and

		tag = #{category}


order by create_date desc
limit #{limit} offset #{offset}

mapper.update:
update `groups`
set hit = hit + 1
where id = #{id}
